public class Validation
{
    public static string[] CheckNumberString(string value)
    {
        long min = -1;
        long max = 2147483647;
        if (!Utility.IsValidNumber(value, min, max))
        {
            string message = string.Format("Invalid input: {0}\n\nInput should be an integer number in between {1} to {2}", 
                                           value, min, max);
            return new string[] { message };
        }
        return null;
    }
}

            
            public class Validation
            {
                public static string[] CheckFileNameForFileInput(string value)
                {
                    if ((System.String.Compare(value, 0, "file:", 0, 5, true) == 0))
                    {
                        string path = value.Substring(5);
                        string filename = string.Empty;
                        bool isValid = Utility.IsValidUnixFileName(path, ref filename);

                        if (!isValid)
                        {
                            string message = string.Format("Invalid filename{0}\n\nFilename cannot be empty or contain '/'", 
                                                           (filename.Length == 0) ? "." : ": " + filename);
                            return new string[] { message };
                        }

                        return null;
                    }
                    else
                    {
                        // We don't check name formats in console
                        // One of the reasons is user-defined NTLM separator 
                        // can be configured in adclient (Bug 32652)
                        return null;
                    }
                }
            }

            
