using System;      
using System.Text;

public class Utility
{
private const char BACKSLASH = '\\';

/// <summary>
/// Check for special character '\'
/// </summary>
/// <param name="value"></param>
/// <returns></returns>
public static string[] CheckInvalidLineBreak(string value)
{
  string[] errors = null;
  string trimValue = value.TrimEnd();
  if (trimValue.EndsWith("\\"))
  {
      errors = new string[] { "You cannot enter '\\' character at the end of line." };
  }
  else
  {
    bool invalid = false;
    int index = value.IndexOf(BACKSLASH);
    while (index != -1 && !invalid)
    {
      // check if odd number of '\' exists
      int count = GetConsecutiveBackSlash(value, index);
      if (count % 2 == 1)
      {
	      invalid = true;
      }
      else
      {
	      index += count;
	      if (index < value.Length)
	      {
	        index = value.IndexOf(BACKSLASH, index);
	      }
	      else
	      {
	        index = -1;
	      }
      }
    }

    if (invalid)
    {
        errors = new string[] { "Please escape '\\' character with a backslash '\\'." };
    }
  }

  return errors;

}

/// <summary>
/// Get the number of consecutive backslash 
/// </summary>
/// <param name="value"></param>
/// <param name="index"></param>
/// <returns></returns>
static private int GetConsecutiveBackSlash(string value, int index)
{
int count = 0;
// search backward
for (int i = index; i < value.Length; i++)
{
    char c = value[i];
    if (c == BACKSLASH)
    {
	count++;
    }
    else
    {
	break;
    }
}

return count;
}


/// <summary>
/// Check for AD user name, group name
/// 
/// Names may not consist entirely of periods and/or spaces, or contain these characters: \ / " [ ] : | < > + = ; , ? * @
/// </summary>
/// <param name="name"></param>
/// <returns></returns>
public static bool IsValidADName(string name)
{
char[] invalidChars = new char[] {'\\', '/', '"', '[', ']', ':', '|', '<', '>', '+', '=', ';', ',', '?', '*', '@' };

if (name == null || name.Length == 0)
{
    return false;
}

foreach(char c in name)
{
    foreach (char invalidChar in invalidChars)
    {
	if (c == invalidChar)
	{
	    return false;
	}
    }
}

return true;
}

/// <summary>
/// Check for domain name
/// 
/// Names can contain [A-Z], [a-z], [0-9], -, _
/// </summary>
/// <param name="name"></param>
/// <returns></returns>
public static bool IsValidDomainName(string domainName)
{
string[] names = domainName.Split('.');

foreach(string name in names)
{
    if (name.Length == 0)
    {
	return false;
    }
    
    foreach(char c in name)
    {
	if (!char.IsLetterOrDigit(c) && c != '_' && c != '-')
	{
	    return false;
	}
    }
}

return true;
}

/// <summary>
/// Check for AD user name
/// e.g. user, user@domain
/// </summary>
/// <param name="name"></param>
/// <returns></returns>
public static bool IsValidADUserName(string fullname)
{
if (fullname == null || fullname.Length == 0)
{
    return false;
}

string name = fullname;
string domain = string.Empty;
int index = fullname.IndexOf('@');
if (index != -1)
{
    name = fullname.Substring(0, index);
    domain = fullname.Substring(index+1, fullname.Length - index - 1);            
}

// check BOTH user and domain names
bool valid = IsValidADName(name);
if (domain.Length > 0)
{
    valid &= IsValidDomainName(domain);
}

return valid;
}



/// <summary>
/// Check for unix user name, group name
/// 
/// only letters, numbers, hyphens, underscores and periods are allowed
/// </summary>
/// <param name="name"></param>
/// <returns></returns>
public static bool IsValidUnixUserName(string name)
{
if (name == null || name.Length == 0)
{
    return false;
}

foreach(char c in name)
{
    if (!char.IsLetter(c) && !char.IsDigit(c) && c != '-' && c != '_' && c != '.')
    {
	return false;
    }
}

return true;
}

/// <summary>
/// Check if the unix file path contains invalid file name
///     return true if valid, false if invalid
///     return the filename
/// </summary>
/// <param name="name"></param>
/// <returns></returns>
public static bool IsValidUnixFileName(string filePath, ref string fileName)
{
bool isValid = true;
fileName = "";

if (filePath == null)
{
    return false;
}

// remove any whitespaces
filePath = filePath.Trim();
if (filePath.Length == 0)
{
    return false;
}

int startIndex = filePath.Length - 1;

// check if the last character is '/'
if (filePath[filePath.Length - 1] == '/')
{
    isValid = false;
    startIndex = startIndex - 1;
}

int index = -1;
if (startIndex >= 0)
{
    index = filePath.LastIndexOf('/', startIndex);
}

if (index != -1)
{
    fileName = filePath.Substring(index + 1, filePath.Length - index - 1);
}
else
{
    fileName = filePath;
}

return isValid;
}

/// <summary>
/// Check for a list of AD user/group names separated by seps
/// </summary>
/// <param name="value"></param>
/// <param name="seps"></param>
/// <param name="displayText"></param>
/// <returns></returns>
public static string[] CheckADNames(string value, char[] seps, string displayText, bool checkDomain)
{
if (value != null && value.Length > 0)
{
    StringBuilder sb = new StringBuilder();
    int count = 0;

    // the names are separated by seps
    string[] names = value.Split(seps, StringSplitOptions.RemoveEmptyEntries);
    foreach (string name in names)
    {
	string trimName = name.Trim();
	bool valid = false;

	// check AD name
	if (checkDomain)
	{
	    valid = Utility.IsValidADUserName(trimName);
	}
	else
	{
	    valid = Utility.IsValidADName(trimName);                
	}

	if (!valid)
	{
	    count++;

	    if (trimName.Length > 0)
	    {
		if (sb.Length > 0)
		{
		    sb.Append(", ");
		}
		sb.Append(trimName);
	    }
	}
    }

    if (count > 0)
    {
	string message = string.Empty;
	string invalidNames = sb.ToString();
	if (checkDomain)
	{
	    message = string.Format("Invalid {0}{1}{2}\n\nDomain name can only contain letters, numbers, '-' and '_'.\n{3} cannot be empty, consist entirely of periods and/or spaces, or contain these characters:\n\\ / \" [ ] : | < > + = ; , ? * @",
					    displayText, 
					    (count == 1) ? "" : "s", 
					    (invalidNames.Length == 0) ? "." : ": " + invalidNames, 
					    displayText);
	}
	else
	{
	    message = string.Format("Invalid {0}{1}{2}\n\n{3} cannot be empty, consist entirely of periods and/or spaces, or contain these characters:\n\\ / \" [ ] : | < > + = ; , ? * @",
					    displayText, 
					    (count == 1) ? "" : "s", 
					    (invalidNames.Length == 0) ? "." : ": " + invalidNames, 
					    displayText);
	}
	return new string[] { message };
    }
}

return null;
}


/// <summary>
/// Check for a list of Unix names separated by seps
/// </summary>
/// <param name="value"></param>
/// <param name="seps"></param>
/// <param name="displayText"></param>
/// <returns></returns>
public static string[] CheckUnixNames(string value, char[] seps, string displayText)
{
if (value != null && value.Length > 0)
{
    StringBuilder sb = new StringBuilder();
    int count = 0;

    // the names are separated by seps
    string[] names = value.Split(seps, StringSplitOptions.RemoveEmptyEntries);
    foreach (string name in names)
    {
	string trimName = name.Trim();

	// check for unix name
	if (!Utility.IsValidUnixUserName(trimName))
	{
	    count++;

	    if (trimName.Length > 0)
	    {
		if (sb.Length > 0)
		{
		    sb.Append(", ");
		}
		sb.Append(trimName);
	    }
	}
    }

    if (count > 0)
    {
	string invalidNames = sb.ToString();
	string message = string.Format("Invalid {0}{1}{2}\n\n{3} cannot be empty and can only contain letters, numbers, '-', '_' and '.'", 
					displayText, 
					(count == 1)? "" : "s", 
					(invalidNames.Length == 0) ? "." : ": " + invalidNames, 
					displayText);

	return new string[] { message };
    }
}

return null;
}

///<summary>
///Check for home directory permission entered
///</summary>
///<param name="value"></param>
///<returns></returns>
public static string[] CheckHomeDirectoryPermission(string value)
{
    string[] MESSAGE = new string[] { "The value of home directory permission must be 3-digit octal numbers with prefix '0' (0xxx)" };
    value = value.Trim();            
    if (string.IsNullOrEmpty(value))
    {                
            return MESSAGE;
    }
    if (value.Length != 4)
    {
            return MESSAGE;
    }
            
    if (!value.StartsWith("0"))
    {
            return MESSAGE;
    }                        
            
    Char[] chars= value.ToCharArray();        
    foreach (char ch in chars)
    {                
         if (!(char.IsDigit(ch) && ch < '8'))
         {
                return MESSAGE;
         }
     }

     return null;
}

/// <summary>
/// Check for a list of UNIX file names separated by seps
/// </summary>
/// <param name="value"></param>
/// <param name="seps"></param>
/// <param name="displayText"></param>
/// <returns></returns>
public static string[] CheckUnixFileNames(string value, char[] seps)
{
if (value != null && value.Length > 0)
{
    StringBuilder sb = new StringBuilder();
    int count = 0;

    // the filepaths are separated by seps
    string[] filepaths = value.Split(seps, StringSplitOptions.RemoveEmptyEntries);
    foreach (string filepath in filepaths)
    {
	string filename = "";
	// check unix filename
	if (!Utility.IsValidUnixFileName(filepath, ref filename))
	{
	    count++;

	    if (filename.Length > 0)
	    {
		if (sb.Length > 0)
		{
		    sb.Append(", ");
		}
		sb.Append(filename);
	    }
	}
    }

    if (count > 0)
    {
	string name = sb.ToString();
	string message = string.Format("Invalid filename{0}{1}\n\nFilename cannot be empty or contain '/'",
					(count == 1) ? "" : "s",
					(name.Length == 0) ? "." : ": " + name);
	return new string[] { message };
    }
}

return null;
}

/// <summary>
/// Check for centrifydc.conf property name
/// </summary>
/// <param name="value"></param>
/// <returns></returns>
public static string[] CheckProperty(string value)
{
if (value != null && value.Length > 0)
{
    bool valid = false;

    valid = Utility.IsValidRegValue(value);

    if (!valid)
    {
        string message = ("Invalid property name.\n\nProperty name can only contain letters, numbers, '.', '-' and '_'.\nIt cannot be empty, consist entirely of periods and/or spaces, or contain these characters:\n\\ / \" [ ] : | < > + = ; , ? * @");
        return new string[] { message };
    }
}

return null;
}

/// <summary>
/// Check for registry value name
/// 
/// Registry value name may not consist entirely of periods and/or spaces, or contain these characters: \ / " [ ] : | < > + = ; , ? * @
/// </summary>
/// <param name="value"></param>
/// <returns></returns>
public static bool IsValidRegValue(string value)
{
char[] invalidChars = new char[] {'\\', '/', '"', '[', ']', ':', '|', '<', '>', '+', '=', ';', ',', '?', '*', '@' };

if (value == null || value.Length == 0)
{
    return false;
}

foreach(char c in value)
{
    foreach (char invalidChar in invalidChars)
    {
	if (c == invalidChar)
	{
	    return false;
	}
    }
}

return true;
}

/// <summary>
/// Check for FQDN
/// </summary>
/// <param name="value"></param>
/// <returns></returns>
public static string[] CheckFQDN(string value)
{
if (value != null && value.Length > 0)
{
    bool valid = false;

    valid = Utility.IsValidDomainName(value);

    if (!valid)
    {
        string message = ("Invalid fully qualified domain name.\n\nFully qualified domain name can only contain letters, numbers, '.', '-' and '_'.\nIt cannot be empty, consist entirely of periods and/or spaces, or contain these characters:\n\\ / \" [ ] : | < > + = ; , ? * @");
        return new string[] { message };
    }
}

return null;
}

/// <summary>
/// Check for Domain name
/// </summary>
/// <param name="value"></param>
/// <returns></returns>
public static string[] CheckDomainName(string value)
{
    string[] MESSAGE = new string[] { "Invalid Domain Name\n\nDomain name can only contains letters, numbers, '-' and '_'." };
    if (!IsValidDomainName(value))
    {
        return MESSAGE;
    }    
    return null;
}

/// <summary>
/// Check for HostNames
/// </summary>
/// <param name="value"></param>
/// <returns></returns>
public static string[] CheckHostNames(string value, char[] seps, string displayText)
{
    string[] MESSAGE = new string[] { "Invalid separator\n\nHostnames should be separated by one space character." };
    if (value != null && value.Length > 0 )
    {
        int index = value.IndexOf("  ");
        if (index >= 0 )
        {
            return MESSAGE;
        }
    }
    
    StringBuilder sb = new StringBuilder();
    int count = 0;
    
    // the names are separated by seps
    string[] names = value.Split(seps, StringSplitOptions.RemoveEmptyEntries);
    foreach (string name in names)
    {
        string trimName = name.Trim();
        bool valid = false;
        valid = Utility.IsValidDomainName(trimName);
        if (!valid)
        {
            count++;
            if (trimName.Length > 0)
            {
                if (sb.Length > 0)
                {
                    sb.Append(", ");
                }
                sb.Append(trimName);
            }
        }
    }    

    if (count > 0)
    {
        string message = string.Empty;
        string invalidNames = sb.ToString();
        message = string.Format("Invalid {0}{1}{2}\n\n{3} can only contains letters, numbers, '-' and '_'. ",
                                    displayText,
                                    (count == 1) ? "" : "s",
                                    (invalidNames.Length == 0) ? "." : ": " + invalidNames,
                                    displayText); 
        return new string[] { message };
    }

    return null;
}

/// <summary>
/// Check if the number is valid and within range
/// </summary>
/// <param name="value"></param>
/// <param name="min"></param>
/// <param name="max"></param>
/// <returns></returns>
public static bool IsValidNumber(string value, long min, long max)
{
    string numberString = value;
    // Removes all leading and trailing white-space characters from the current string
    numberString.Trim();

    long numberConverted = 0;
    // Method will return false when number less than MinValue or greater than MaxValue
    bool validNumber = long.TryParse(numberString, out numberConverted);
    if (validNumber)
    {
        if (numberConverted >= min && numberConverted <= max)
        {
            return true;
        }
    }
    return false;
}

}
